userDefinedLinkHintCss:
div > .vimiumHintMarker {
  /* linkhint boxes */
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#FFF785),
  color-stop(100%,#FFC542));
  border: 1px solid #E3BE23;
}

div > .vimiumHintMarker span {
  /* linkhint text */
  color: black;
  font-weight: bold;
  font-size: 12px;
}

input#vomnibarInput.vimiumReset {
	color: #FFF !important;
  background-color: #111 !important;
}

#vomnibar {
   border-color: black;
   background-color: black;
}

#vomnibar .vomnibarSearchArea {
   background-color: #222;
   border-bottom: #3f3f3f;
}

#vomnibar input {
   border-color: #393939;
}

#vomnibar ul {
    background-color: #222;
}

#vomnibar li {
   border-bottom: #3f3f3f;
}

#vomnibar li .vomnibarUrl {
   color: #5287e3;
}

#vomnibar li .vomnibarMatch {
   color: white;
}

#vomnibar li.vomnibarSelected {
    background-color: #181D24;
}

#vomnibar li em .vomnibarMatch, #vomnibar li .vomnibarTitle .vomnibarMatch {
   color: white;
}

#vomnibar li em, #vomnibar li .vomnibarTitle {
   color: #ddd;
}


div > .vimiumHintMarker {
  background: #131313;
  font-size: 12px;
  border: 0.25em solid #383c4a;
  border-radius: 0.68em;
  box-shadow: 0em 0.1em 0.6em 0.1em rgba(0, 0, 0, 0.4);
}

div > .vimiumHintMarker span {
  color: #c9c9c9;
  font-size: inherit;
  text-shadow: none;
}

div > .vimiumHintMarker > .matchingCharacter {
  color: #ffbb33;
}

div.vimiumHUD {
    display: block;
    position: fixed;
    width: 100%;
    bottom: 8px;
    left: 8px;
    border-radius: 19px;
    box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.8);
    border: 1px solid #aaa;
    z-index: 2140000000;
}

div.vimiumHUD > .vimiumHUDSearchArea {
    padding: 0px;
}

div.vimiumHUD > .vimiumHUDSearchArea > .vimiumHUDSearchAreaInner {
    color: #c9c9c9;
    font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
    font-size: 14px;
    height: 30px;
    margin-bottom: 0;
    padding: 2px 4px;
    border-radius: 3px;
    background-color: #131313;
    width: 100%;
    outline: none;
    box-sizing: border-box;
    text-align: center;
    line-height: 20px;
}
